examples.dematerialize:
const { rxObserver } = require('api/v0.3');
const { timer, throwError, Notification } = require('rxjs');
const { switchMap, materialize, dematerialize, delay, map } = require('rxjs/operators');


const source$ = timer(5).pipe(
  switchMap(() => throwError('Err!'))
);

const result$ = source$.pipe(
  // turn all events on stream into Notifications
  materialize(),
  // delay error by 5ms
  delay(5),
  // turn error into a value
  map(n => new Notification('N', n.error, undefined)),
  // turn Notifications back to events on stream
  dematerialize()
);

source$.subscribe(rxObserver('source$'));
result$.subscribe(rxObserver('result$'));

examples.repeat:
const { rxObserver } = require('api/v0.3');
const { timer, iif, of } = require('rxjs');
const { repeat, delay } = require('rxjs/operators');

// repeat:
// resubscribe when source stream completes

// basic example
timer(5)
  .pipe(
    repeat(5)
  )
  .subscribe(rxObserver());

// when source stream completes -- repeat
// subscribes again and gets a new stream
iif(
  () => Date.now() < 10
  , of('0..10')
  , of('10+')
)
  .pipe(
    delay(5),
    repeat(5)
  )
  .subscribe(rxObserver());

