examples.retryWhen:
const {rxObserver} = require('api/v0.3');
const { timer } = require('rxjs');
const { map, tap, retryWhen, delayWhen } = require('rxjs/operators');


const source$ =
  timer(0, 100).pipe(
    map(val => {
      if (val > 2) {
        // error will be picked up by retryWhen
        throw val;
      }
      return val;
    }),
    retryWhen(errors =>
      // here Errors are transformed into Events
      errors.pipe(
        // show error messages thread
        tap(rxObserver('ERROR MESSAGES')),
        // will restart in 150 ms
        delayWhen(val => timer(val * 50))
      )
    )
);

source$.subscribe(rxObserver('source$'));
// taken from
// https://www.learnrxjs.io/operators/error_handling/retrywhen.html

examples.share:
const { rxObserver } = require('api/v0.3');
const { palette } = require('rp-api/colors');
const { Observable } = require('rxjs/Rx');

const palette$ = Observable.from(palette);

const cold$ = Observable.timer(0, 5)
  // add color to items
  .zip(palette$, (value,color)=>({ valueOf(){ return value; }, color}));

const hot$ =  cold$.share();

// creating observers for cold$
const a = rxObserver();
const b = rxObserver();
cold$.take(5).subscribe(a);

// creating observers for hot$
const c = rxObserver();
const d = rxObserver();
hot$.take(5).subscribe(c);

// delayed subscriptions
setTimeout(()=>{
  cold$.take(5).subscribe(b);
  hot$.take(5).subscribe(d);
}, 10);

